package com.syntax.reviewclass;

public class OperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Operators in Java Arithmetic Operators + - / * % Logical Operator == != >= <= > <
		// Logical operataori kullandiginda sana true ya da false yanitlarini verir. Asagidaki 
		
		int num=10;
		int num2=10;
		System.out.println(num+num2);
		System.out.println(num>=num2); // num>num2=>false or num=num2=>true
		
		
		//
		System.out.println(Long.MAX_VALUE);
		System.out.println(Long.MIN_VALUE);
		System.out.println(Byte.MAX_VALUE);
		System.out.println(Integer.MAX_VALUE);
		System.out.println(num/num2);
		System.out.println(num%num2);
		
		System.out.println(10/3);
		System.out.println(10%3);
		
		System.out.println(10.0/3);
		System.out.println((int)10.0/3);
		System.out.println(10.5*3);
		
		
		
	}

}
