package com.syntax.reviewclass;

public class ReviewClass2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String firstName="Ali";
		String lastName="Baba";
		String fullName=firstName+lastName;
		int num=10;
		int num2=10;
		System.out.println(fullName);
		System.out.println(firstName+num);	
		System.out.println(firstName+num+num2);
		System.out.println(firstName+(num+num2)); // when you add parantez java make procedure to add/sub number/If you dont add parantez java dont make procedure to sub number
		System.out.println(num+firstName);	
		
		String name="Run";
		String name2="Forest";
		System.out.println(name+name2+name);
		
	}

}
